var gulp = require('gulp');
var gutil = require('gulp-util');
var babel = require('gulp-babel');
var clean = require('gulp-clean');
var path = require('path');
var fs = require('fs');
var name = require('./package.json').name;
var babelrc = JSON.parse(fs.readFileSync('./.babelrc', 'utf-8'));

gulp.task('build-clean', function method() {
  return gulp.src(['dist', path.join('node_modules', name)])
  .on('error', gutil.log) // 在这里捕捉编译错误
  .pipe(clean());
});

gulp.task('build', function method() {
  return gulp.src(['src/**/*.js'])
  // .pipe(sourcemaps.init())
  .pipe(babel({
    plugins: babelrc.env.node.plugins
  }))
  .on('error', gutil.log) // 在这里捕捉编译错误
  // .pipe(sourcemaps.write('.'))
  .pipe(gulp.dest('dist'));
});

gulp.task('default', ['build']);
